package week1.day1;

public class negativeToPositive {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i= 0;
		int positiveNumber;
		if(i<0)
		{
			positiveNumber= i*(-1);
		System.out.println( "number is changed to positive");
		System.out.println("number is negative");
		}
		else if(i>0)
		{
		System.out.println("number is positive");
		}
		else {
			 System.out.println("number neither positive or negative");
		}
		
	}
}
	


